# --- Global State ---
board = [
    ["", "", ""],
    ["", "", ""],
    ["", "", ""]
]
current_player = "X"
winner = None

def print_board():
    for row in board:
        print(row)

def make_move(row, col):
    """Places the current player's mark at (row, col)."""
    global current_player
    # TODO: Check if cell is empty
    if board[row][col] == "":
        board[row][col] = current_player
        # Switch player
        current_player = "O" if current_player == "X" else "X"
        return True
    return False

def check_winner():
    """Returns 'X', 'O', or None."""
    # TODO: Check Rows
    for row in board:
        if row[0] == row[1] == row[2] and row[0] != "":
            return row[0]

    # TODO: Check Columns
    for col in range(3):
        if board[0][col] == board[1][col] == board[2][col] and board[0][col] != "":
            return board[0][col]

    # TODO: Check Diagonals
    if board[0][0] == board[1][1] == board[2][2] and board[0][0] != "":
        return board[0][0]
    if board[0][2] == board[1][1] == board[2][0] and board[0][2] != "":
        return board[0][2]

    return None

# --- Simulation ---
moves = [(1, 1), (0, 0), (0, 1), (0, 2), (2, 1)]
for r, c in moves:
    make_move(r, c)
    w = check_winner()
    if w:
        winner = w
        break

# For Visualizer
game_state = {
    "board": board,
    "winner": winner
}
